mapper.select:
SELECT DATE_FORMAT(e.create_time, '%Y-%m-%d') AS date,
count(1) totalNum,
count(DISTINCT e.vip_id) peopleNum,
count(0 != e.ticket_id OR NULL) luckyNum
FROM tb_wap_event e
WHERE e.type = #{flopBo.type}
AND e.source = #{flopBo.source}
AND e.create_time BETWEEN #{flopBo.startTime}
    AND #{flopBo.endTime}

GROUP BY DATE_FORMAT(e.create_time, '%Y-%m-%d');

SELECT COUNT(1)
FROM tb_wap_card_record
WHERE group_id = #{groupId}
  AND source = #{source}

SELECT DATE_FORMAT(t.create_time, '%Y-%m-%d') AS date,
count(1) pv,
count(DISTINCT t.open_id) uv
FROM tb_wap_record_view t
WHERE t.source = #{sourcePvUvBo.source}
AND t.create_time BETWEEN
    #{sourcePvUvBo.startTime}
    AND #{sourcePvUvBo.endTime}

GROUP BY DATE_FORMAT(t.create_time, '%Y-%m-%d');

SELECT m.province, m.city, m.mall_name, m.oms_code, u.mobile, e.create_time time, e.ticket_grade grade, e.type, e.ticket_info FROM tb_wap_event e LEFT JOIN tb_wap_user u ON u.vip_id = e.vip_id LEFT JOIN tb_wap_mall m ON m.oms_code = e.oms_code WHERE e.ticket_id != 0 AND e.type like '%${luckyBo.type}%' AND e.source = #{luckyBo.source}
AND m.province like '%${luckyBo.province}%'
AND m.city like '%${luckyBo.city}%'
AND m.mall_name like '%${luckyBo.mallName}%'
AND u.mobile = #{luckyBo.mobile}
AND e.ticket_grade = #{luckyBo.grade}
AND e.create_time BETWEEN #{luckyBo.startTime} AND #{luckyBo.endTime}
AND e.ticket_get = 1 GROUP BY e.vip_id ORDER BY e.create_time DESC

SELECT
count(1) pv,
count(DISTINCT t.open_id) uv
FROM tb_wap_record_view t
WHERE t.source = #{sourcePvUvBo.source}
AND t.create_time BETWEEN
    #{sourcePvUvBo.startTime}
    AND #{sourcePvUvBo.endTime}
