mapper.update:
update tb_wap_act_mall_merge
set is_join =#{isJoin}
where act_code =#{actCode}

update.foreach:
update tb_wap_act_mall_merge
set is_join = #{list.isJoin},
update_time = now()
where oms_code=#{list.omsCode}
and act_code = #{actCode}

mapper.update:
update tb_wap_mall
   set default_enable = 0
   where city = #{city}
